// Create the MoveCounter component
export default function MoveCounter({ history, currentMove }) {
    // Calculate the number of moves for each player
    const calculateMoveCount = () => {
      let xMoves = 0;
      let oMoves = 0;
  
      // Iterate through the history up to the current move index
      for (let i = 0; i <= currentMove; i++) {
        // Get the squares for the current move
        const squares = history[i];
        
        // Count the number of moves for each player
        for (let square of squares) {
          if (square === 'X') {
            xMoves++;
          } else if (square === 'O') {
            oMoves++;
          }
        }
      }
  
      return { xMoves, oMoves };
    };
  
    // Calculate the move count
    const { xMoves, oMoves } = calculateMoveCount();
  
    // Render the move count
    return (
      <div className="move-counter">
        <p>Moves made by X: {xMoves}</p>
        <p>Moves made by O: {oMoves}</p>
      </div>
    );
  }
