import Square from "./Square";
import { calculateWinner } from "./path/to/your/calculateWinner"; // Import calculateWinner function

export default function Board({ xIsNext, squares, onPlay }) {
    // Calculate the winner and the winning line
    const { winner, winningLine } = calculateWinner(squares);
    
    function handleClick(i) {
        if (winner || squares[i]) {
            return;
        }
        const nextSquares = squares.slice();
        if (xIsNext) {
            nextSquares[i] = 'X';
        } else {
            nextSquares[i] = 'O';
        }
        onPlay(nextSquares);
    }
    
    let status;
    if (winner) {
        status = `Winner: ${winner}`;
    } else {
        status = `Next player: ${xIsNext ? 'X' : 'O'}`;
    }
    
    // Helper function to check if a square is part of the winning line
    function isWinningSquare(index) {
        return winningLine.includes(index);
    }
    
    // Render the board
    return (
        <>
            <div className="status">{status}</div>
            <div className="board-row">
                <Square
                    value={squares[0]}
                    onSquareClick={() => handleClick(0)}
                    isWinning={isWinningSquare(0)} // Pass isWinning prop
                />
                <Square
                    value={squares[1]}
                    onSquareClick={() => handleClick(1)}
                    isWinning={isWinningSquare(1)} // Pass isWinning prop
                />
                <Square
                    value={squares[2]}
                    onSquareClick={() => handleClick(2)}
                    isWinning={isWinningSquare(2)} // Pass isWinning prop
                />
            </div>
            <div className="board-row">
                <Square
                    value={squares[3]}
                    onSquareClick={() => handleClick(3)}
                    isWinning={isWinningSquare(3)} // Pass isWinning prop
                />
                <Square
                    value={squares[4]}
                    onSquareClick={() => handleClick(4)}
                    isWinning={isWinningSquare(4)} // Pass isWinning prop
                />
                <Square
                    value={squares[5]}
                    onSquareClick={() => handleClick(5)}
                    isWinning={isWinningSquare(5)} // Pass isWinning prop
                />
            </div>
            <div className="board-row">
                <Square
                    value={squares[6]}
                    onSquareClick={() => handleClick(6)}
                    isWinning={isWinningSquare(6)} // Pass isWinning prop
                />
                <Square
                    value={squares[7]}
                    onSquareClick={() => handleClick(7)}
                    isWinning={isWinningSquare(7)} // Pass isWinning prop
                />
                <Square
                    value={squares[8]}
                    onSquareClick={() => handleClick(8)}
                    isWinning={isWinningSquare(8)} // Pass isWinning prop
                />
            </div>
        </>
    );
}
