export default function MoveCounter({ history, currentMove }) {
    // Initialize move counts for each player
    let xMoves = 0;
    let oMoves = 0;

    // Track current turn: 'X' starts the game
    let currentPlayer = 'X';

    // Iterate through the history up to the current move index
    for (let i = 0; i <= currentMove; i++) {
        // Get the squares for the current move
        const squares = history[i];

        // Count moves for each player
        for (let j = 0; j < squares.length; j++) {
            const square = squares[j];

            // Increment counters for each player's move
            if (square === 'X' && currentPlayer === 'X') {
                xMoves++;
                currentPlayer = 'O'; // Switch to the other player
            } else if (square === 'O' && currentPlayer === 'O') {
                oMoves++;
                currentPlayer = 'X'; // Switch to the other player
            }
        }
    }

    // Render the move count
    return (
        <div className="move-counter">
            <p>Moves made by X: {xMoves}</p>
            <p>Moves made by O: {oMoves}</p>
        </div>
    );
}
