export default function MoveCounter({ history, currentMove }) {
    // Initialize move counts for each player
    let xMoves = 0;
    let oMoves = 0;

    xMoves = currentMove / 2;
    oMoves = xMoves;
    if (xMoves % 2 === 1)  {
        xMoves = Math.trunc(++xMoves);  // if currentMove === odd then X should be one more than o
        oMoves = Math.trunc(oMoves);
        console.log("currentMove =" + currentMove +  "     NOT even   xMoves=" + xMoves + "   oMoves=" + oMoves);
    } else {
        console.log("currentMove =" + currentMove +  "     ven   xMoves=" + xMoves + "   oMoves=" + oMoves);
    }
/*
    // Iterate through the history up to the current move index
    for (let i = 0; i <= currentMove; i++) {
        // Get the squares for the current move
 
        const squares = history[i];

        // Check if it's the expected player's turn
        const currentPlayer = i % 2 === 0 ? 'X' : 'O';

        // Iterate through the squares in the current move
        for (let square of squares) {
            console.log("square=" + square);
            if (currentPlayer === 'X' && square === 'X') {
                xMoves++;
                console.log("Increment X=" + xMoves);
            } else if (currentPlayer === 'O' && square === 'O') {
                oMoves++;
                console.log("Increment O=" + oMoves);
            }
        }
    }
*/
    // Render the move counts for both players
    return (
        <div className="move-counter">
            <p>Moves made by X: {xMoves}</p>
            <p>Moves made by O: {oMoves}</p>
        </div>
    );
}
